/* Generated SQL SQL ID : TOKN_KEY.insert */
INSERT INTO TOKN_KEY (
  USER_ID /* USER_ID 사용자_ID varchar(50) */
, CLNT_ID /* CLNT_ID 클라이언트_ID varchar(20) */

, KEY_VAL /* KEY_VAL 키_값 varchar(32) */

) VALUES (
  #{userId} /* USER_ID 사용자_ID varchar(50) */
, #{clntId} /* CLNT_ID 클라이언트_ID varchar(20) */

, #{keyVal} /* KEY_VAL 키_값 varchar(32) */

)

mapper.update:
/* Generated SQL SQL ID : TOKN_KEY.update */ UPDATE TOKN_KEY
, KEY_VAL = #{keyVal} /* KEY_VAL 키_값 varchar(32) */
WHERE USER_ID = #{userId} /* USER_ID 사용자_ID varchar(50) */ AND CLNT_ID = #{clntId} /* CLNT_ID 클라이언트_ID varchar(20) */

	MERGE INTO tokn_key X
 USING dual
  ON (
        x.user_id = #{userId}
    AND x.clnt_id = #{clntId}
  )
  WHEN MATCHED THEN
   UPDATE SET key_val = #{keyVal}
  WHEN NOT MATCHED THEN
  insert
   (
        user_id
       ,clnt_id
       ,key_val
   )
   values( #{userId}
          ,#{clntId}
          ,#{keyVal}
  )

/* Generated SQL SQL ID : TOKN_KEY.merge */ INSERT INTO TOKN_KEY ( USER_ID /* USER_ID 사용자_ID varchar(50) */ , CLNT_ID /* CLNT_ID 클라이언트_ID varchar(20) */
